columns.template:
{{#ifEqual type "User Story"}}
{{#ifHasInlinks "satisfaction"}}
{{#eachInlinkWith type="satisfaction"}}
  <div style="margin-top:5px; margin-bottom:5px; padding-left:15px; text-indent: -5px;"><i class="icon-li icon-caret-left"></i> <em>{{#hyperlink}}{{docObjId}}{{/hyperlink}}:</em> {{shortDescription  maxLength=80}}</div>
{{#ifHasInlinks "verification"}}
{{#eachInlinkWith type="verification"}}
  <div style="margin-bottom:3px;margin-left:20px;padding-left:15px; text-indent: -5px;"><i class="icon-li icon-caret-left"></i> <em>{{#hyperlink}}{{docObjId}}{{/hyperlink}}:</em> {{shortDescription maxLength=80}}</div>
{{/eachInlinkWith}}
{{else}}
  <div style="margin-bottom:3px;margin-left:20px;padding-left:15px; text-indent: -5px;"><i class='icon-li icon-warning-sign'></i> Missing verification links</div>
{{/ifHasInlinks}}
{{/eachInlinkWith}}
{{else}}
  <div style="margin-bottom:3px;padding-left:15px; text-indent:-5px;"><i class='icon-li icon-warning-sign'></i> Missing satisfaction links</li></div>
{{/ifHasInlinks}}
{{/ifEqual}}
